INSERT INTO `coin_dev_member` VALUES (178, 'tether', 'Phil Potter', 'Bitfinex 与 Tether 首席战略官。', 'https://s2.gongft.com/images/baike/612647c31b17f1e13bd19ee9f9508d37.jpg', '', 'https://www.linkedin.com/in/phil-potter-0b6756154/', '\n                            <p>Phil Potter 自 1994 年毕业于耶鲁大学，曾经是摩根士丹利的衍生品交易分析师，而后开发自动化投资组合管理技术，在贝尔斯登工作两年以后，他参与创办 Mercury Securities, LLC，担任首席技术官，而后共同创立了 PacMid Technologies、Dimension Capital 等，专注于开发和实施尖端交易技术以及创建成功的自动交易咨询业务。</p>\n<p>2013 年起，他开始涉足比特币相关的项目，起初作为 Bitfinex 大客户做加密货币基金管理，后被说服于 2013 年底加入 Bitfinex，担任首席战略官。同时，他参与开发了 Tether，也担任首席战略官。</p>\n                            \n                        ', 0);
INSERT INTO `coin_dev_member` VALUES (179, 'tether', 'Matthew Tremblay', '首席合规官', '', '', '', '', 0);
INSERT INTO `coin_dev_member` VALUES (180, 'monero', 'Riccardo Spagni', 'Monero 核心开发，项目负责人', 'https://s2.gongft.com/images/baike/fe605be47d32b8969212067fc1ddc898.jpg', 'https://twitter.com/fluffypony', '', '<p>Riccardo Spagni 是 Monero 核心团队成员，也是 Monero 项目目前的管理者、主要维护者。他具有信息学与物流的背景，早于 2011 年初开始参与比特币开发，2012 年参与到各种加密货币相关的项目开发中，并在 2017 年推出了 Globee 多币种支付处理器。</p>\n', 0);
