INSERT INTO `coin_dev_member` VALUES (182, 'chiliz', 'Max Rabinovitch', '首席战略官', 'https://s2.gongft.com/images/baike/2C355E36A597A9F58CA7BDC78B9D0A28.png?v=1591684815', '', 'https://www.linkedin.com/in/max-rabinovitch/', '', 0);
INSERT INTO `coin_dev_member` VALUES (183, 'ethereum', 'Gavin Wood', '创始人兼首席技术官', 'https://s2.gongft.com/image/20190621/4731561099774617.png', 'https://twitter.com/gavofyork', 'https://www.linkedin.com/in/gavin-wood-88843316/', '', 0);
INSERT INTO `coin_dev_member` VALUES (184, 'ethereum', 'Jeffrey Wilcke', '创始人和Go-Ethereum领导', 'https://s2.gongft.com/image/20190621/8231561099283368.png', 'https://twitter.com/jeffehh', 'https://www.linkedin.com/in/jeffreywilcke', '<p>Jeffrey Wilcke是以太坊的创始人之一。他于2013年开始使用Go编程语言首次实现以太坊，并一直担任Go团队负责人和首席开发人员。Go客户端于2015年7月30日成功推出，标志着创世块和以太坊平台的发布。</p>\n', 0);
INSERT INTO `coin_dev_member` VALUES (185, 'ethereum', 'Anthony Di lorio', '创始人', 'https://s2.gongft.com/image/20190621/2281561099563578.png', 'https://twitter.com/diiorioanthony', 'https://www.linkedin.com/in/anthonydiiorio1/', '', 0);
INSERT INTO `coin_dev_member` VALUES (186, 'ethereum', 'Joseph Lubin', '创始人', 'https://s2.gongft.com/image/20190621/7931561098887167.png', 'https://twitter.com/ethereumjoseph', 'https://www.linkedin.com/in/joseph-lubin-48406489', '<p>Joseph Lubin，就职于ConsenSys，现任创始人一职。作为行业享有盛名的大咖，Joseph Lubin行事低调，对工作热情饱满，多次受邀作为嘉宾出席各类大会，并发表了精彩演讲。<br />\n2016年09月19日，Joseph Lubin受邀参加了由以太坊基金会在虹口区黄浦路199号主办的《2016上海区块链国际周(Shanghai International Blockchain Week)》，并发表了精彩演讲。</p>\n', 0);
INSERT INTO `coin_dev_member` VALUES (187, 'ethereum', 'Vlad Zamfir', '以太坊开发团队的核心成员。', 'https://s2.gongft.com/images/baike/7c972b50029c452c747c8ff805e8cb1c.jpg', 'https://twitter.com/vladzamfir', 'https://www.linkedin.com/in/vlad-zamfir/', '<p>Vlad Zamfir 是以太坊开发团队的核心成员，也是博雅公关公司的数据分析顾问。他积极参与以太坊社区，工作主要聚焦于以太坊生态中的「加密经济学」、PoS 股权证明、Sharding 分片等。</p>\n\n<p>自 2014 年以来，Vlad Zamfir 一直是以太坊基金会的研究员，他是 Casper 协议升级的首席开发者。</p>\n', 0);
INSERT INTO `coin_dev_member` VALUES (188, 'ethereum', 'Mihai Alisie', '以太坊、Bitcoin Magazine 联合创始人。', 'https://s2.gongft.com/images/baike/8e6708fd017360dc61c2dacf0eaebb27.jpg', 'https://twitter.com/MihaiAlisie', 'https://www.linkedin.com/in/mihaialisie/', '<p>Mihai Alisie 是以太坊、Bitcoin Magazine 的联合创始人，目前专注于使 Akasha 项目的梦想成为现实。他自 2011 年以来一直积极参与区块链技术，当时，他与 Vitalik Buterin 共同创建了世界上首个以比特币为中心的出版物 Bitcoin Magazine，由他担任该杂志的主编，直至 2013 年底，他也加入 Vitalik 创立的以太坊项目。</p>\n\n<p>在以太坊的早期，Mihai Alisie 领导瑞士创建对以太坊众筹活动至关重要的商业基础设施和法律框架。此后，他监督了以太坊的战略，和以太坊基金会副主席的工作。直到 2015 年底，他将注意力转向 AKASHA 项目。</p>\n', 0);
INSERT INTO `coin_dev_member` VALUES (189, 'ethereum', 'Gustav Simonsson', '以太坊的核心开发者之一。', 'https://s2.gongft.com/images/baike/2a688fea428828654e6e19a11df10b7c.jpg', 'https://twitter.com/classygustav', '', '<p>Gustav Simonsson 为以太坊的核心开发者，具体工作涉及共识协议、核心客户端和网络安全方面，专注于以太坊区块链的安全性、Golang 客户端，确保平台的安全性。</p>\n\n<p>他拥有计算机科学背景，自 2011 年以来一直致力于开发复杂的后端系统，并在金融科技、支付处理、电信与区块链系统上积累经验。</p>\n', 0);
INSERT INTO `coin_dev_member` VALUES (190, 'ethereum', 'Lane Rettig', '以太坊 Ewasm 独立开发者。', 'https://s2.gongft.com/images/baike/3746edc0a3deab4ab806b0e561783ae1.jpg', 'https://twitter.com/lrettig', '', '\n                            <p>Lane Rettig 是独立的以太坊核心开发者，也是 Ewasm 团队的成员之一，专注于为以太坊 2.0、分片等相关工作。他主要从事有关用户及开发者的体验设计、治理和社交可扩展性的独立研究。</p>\n<p>另外，他是纽约曼哈顿的加密社区 Crypto NYC 的创始人，致力于使所有人都能使用 Web 3.0 与区块链技术。他是黑客、马拉松跑者、冒险家与企业家、经济学家、思想家、作家，掌握多门语言，还是沃顿商学院 MBA 学生。</p>\n                            \n                        ', 0);
INSERT INTO `coin_dev_member` VALUES (191, 'ethereum', 'Hudson Jameson', '以太坊开发者，专注于 Solidity 编程语言。', 'https://s2.gongft.com/images/baike/93fbe60c9375abf3057fb1a577ef2dab.jpg', 'https://twitter.com/hudsonjameson', 'https://www.linkedin.com/in/hudsonjameson/', '\n                            <p>以太坊开发者，专注于 Solidity 编程语言，工作在 Oaken Innovations 与以太坊基金会。</p>\n                            \n                        ', 0);
INSERT INTO `coin_dev_member` VALUES (192, 'ethereum', 'Martin Holst Swende', '以太坊安全主管。', 'https://s2.gongft.com/images/baike/72581667eb490c51dcba1c3f7f423b5e.jpg', 'https://twitter.com/mhswende', 'https://www.linkedin.com/in/martin-holst-swende-a6664/', '<p>Martin Holst Swende，以太坊基金会安全部门主管。</p>', 0);
INSERT INTO `coin_dev_member` VALUES (193, 'ethereum', 'Jake Lang', '在以太坊核心开发与研究者，主要从事 Ewasm 开发。', 'https://s2.gongft.com/images/baike/ee7e3b8a5fbe25a315d95f845f929715.jpg', 'https://twitter.com/jake_theripper', 'https://www.linkedin.com/in/jak3lang/', '\n                            <p>Jake Lang 在以太坊，从事 Ewasm 开发，也在 etclabscore 上使用 SputnikVM JIT，亦是 Wasm-chisel 共同维护者。 他对 Rust、WebAssembly、OSDev 以及加密的所有东西都感兴趣。</p>\n                            \n                        ', 0);
INSERT INTO `coin_dev_member` VALUES (194, 'ethereum', 'Alex Beregszaszi', '以太坊开发者。', 'https://s2.gongft.com/images/baike/f927a441b4e168b7d9a80ed6378aa382.jpg', 'https://twitter.com/alexberegszaszi', 'http://ie.linkedin.com/in/alexberegszaszi', '\n                            <p>以太坊开发者，常出现在都柏林、柏林以及区块链上。更具体地说，是出现在 Solidity 与 Ewasm 上。懒得发推特，更多在 Github 上。</p>\n                            \n                        ', 0);
INSERT INTO `coin_dev_member` VALUES (195, 'ethereum', 'Mister Ligi', '以太坊开发者。', 'https://s2.gongft.com/images/baike/967645ff1234359058c78e2da35e2194.jpg', 'https://twitter.com/mr_ligi', '', '\n                            <p>Mister Ligi 是以太坊开发者。</p>\n                            \n                        ', 0);
INSERT INTO `coin_dev_member` VALUES (196, 'ethereum', 'Aya Miyaguchi', '以太坊基金会执行总监。', 'https://s2.gongft.com/images/baike/7b60ede6e6ee89fa9a9d4bded68bcafe.jpg', 'https://twitter.com/mi_ayako', 'https://www.linkedin.com/in/amiyaguchi/', '\n                            <p>Aya Miyaguchi 目前是以太坊基金会执行总监，曾在 Kraken 交易平台全权负责日本地区的运营工作。</p>\n                            \n                        ', 0);
INSERT INTO `coin_dev_member` VALUES (197, 'ethereum', 'Joey Zhou', '以太坊开发的鼻祖级人物。', 'https://s2.gongft.com/images/baike/c77980f9b5e0396f418985756ab87902.jpg', 'https://twitter.com/josephzhou', 'https://www.linkedin.com/in/josephyzhou/', '\n                            <p>Joey Zhou 是以太坊开发的鼻祖级人物，早在 2014 年，编写了以太坊上的首个节点实现，并为 Geth、Go-Ethereum、CPP-Ethereum 协议的开发做出了重要贡献。另外，他是 Cyborgenic 咨询公司的管理合伙人，专注于金融科技创新，亦是一位活跃的天使投资人和创业顾问。</p>\n                            \n                        ', 0);
INSERT INTO `coin_dev_member` VALUES (198, 'ethereum', 'Péter Szilágyi', '以太坊核心开发者。', 'https://s2.gongft.com/images/baike/78964ccd6dfea14bf93ba0bb85593659.jpg', 'https://twitter.com/peter_szilagyi', 'https://www.linkedin.com/in/karalabe/', '\n                            <p>Péter Szilágyi，代号 Karalabe，以太坊的核心开发者，另外，他也是分布式通讯应用 Project Iris 的作者、RegionRank 创始人、Ether API 共同创建者。</p>\n                            \n                        ', 0);
INSERT INTO `coin_dev_member` VALUES (199, 'ethereum', 'Greg Colvin', '号称「以太坊甘道夫」。', 'https://s2.gongft.com/images/baike/d4992988ae2587bab1fdac0a1c972396.jpg', 'https://twitter.com/greg_colvin', 'https://www.linkedin.com/in/gregcolvin/', '\n                            <p>「以太坊甘道夫」之称的工程师 Greg Colvin，现为 Algorand 项目理论团队成员，凭借三十多年的计算机软件研发经验，帮助项目创建原子交换、智能合约、虚拟机等。Colvin 拥有康奈尔大学的定量心理学博士学位，以及科罗拉多大学心理学和计算机科学学士学位，并在 ANSI/ISO c++标准委员会任职多年，在企业软件开发公司甲骨文是负责创建 Oracle Java 虚拟机的团队的主要成员，最近参与了以太坊虚拟机性能的改进，并致力于提高以太坊生态系统中流程的透明度。</p>\n                            \n                        ', 0);
INSERT INTO `coin_dev_member` VALUES (200, 'ethereum', 'Fabian Vogelsteller', 'LUKSO 创始人，以太坊项目从事 Ðapp 开发。', 'https://s2.gongft.com/images/baike/aca18a88aa777d83bbe989adc84daa59.jpg', 'https://twitter.com/feindura', 'https://www.linkedin.com/in/fabian-vogelsteller-46365042/', '<p>Fabian Vogelsteller 是 LUKSO 创始人、作者，从前在 Mist、Web3.js 以及以太坊项目从事 &ETH;app 开发。</p>\n', 0);
INSERT INTO `coin_dev_member` VALUES (201, 'ethereum', 'Raul Jordan', 'Prysmatic Labs 联合创始人，以太坊开发者。', 'https://s2.gongft.com/images/baike/aaaa000ca9c606014b15771281620c77.jpg', 'https://twitter.com/raulitojordan', '', '<p>Raul Jordan 是 Prysmatic Labs 联合创始人，也兼任 zk Capital 合伙人，毕业于哈佛，区块链工程师、以太坊开发者，专注于 Prysmatic Labs 分片技术开发。</p>\n', 0);
INSERT INTO `coin_dev_member` VALUES (202, 'ethereum', '平井洋一', '以太坊形式化验证工程师，负责虚拟机项目。', 'https://s2.gongft.com/images/baike/f9e61c8ac1daf0e7c646b43cb5ebffae.jpg', 'https://twitter.com/pirapira', '', '\n                            <p>以太坊形式化验证工程师，负责虚拟机项目。</p>\n                            \n                        ', 0);
INSERT INTO `coin_dev_member` VALUES (203, 'ethereum', 'Justin Drake', '以太坊的研究分析师。', 'https://s2.gongft.com/images/baike/26ab39cb5a23b8dc2dfc9a0384da8157.jpg', 'https://twitter.com/drakefjustin', 'https://www.linkedin.com/in/drakefjustin', '\n                            <p>Justin Drake 住在英国剑桥，拥有英国剑桥大学数学系学士和硕士学位。他于 2013 年创立了剑桥比特币聚会小组，并于 2014 年离开了他作为程序员、FPGA 工程师的职位，专心研究区块链领域。2015 年，他运营了比特币 ATM，并成立了 Duo Money，一家为 OpenBazaar 提供网络界面的公司。而现在，他是以太坊基金会的研究分析师，专注于分片。</p>\n<p>他在 18 岁之后定居英国剑桥，是法国人也是意大利人，妻子是俄罗斯人。父亲是一名工程师，英国人，而母亲是一名心理师，阿根廷人。可见 Justin Drake 的成长背景与生活经验十分国际化，自幼在法国长大，中学时期在中国，此后在英国。</p>\n                            \n                        ', 0);
INSERT INTO `coin_dev_member` VALUES (204, 'ethereum', 'Karl Floersch', '以太坊核心开发者。', 'https://s2.gongft.com/images/baike/7240a3cf09c18ab2ac8e145d70f59a7d.jpg', 'https://twitter.com/karl_dot_tech', 'https://www.linkedin.com/in/karlfloersch', '\n                            <p>以太坊核心开发者，年仅 23 岁，主要参与开发以太坊 Casper 以及权益证明机制 PoS 运算法则。除研究 Casper 以外，他爱好冥想和即兴饶舌，从小受到印度教的熏陶，通过冥想来思考和感悟一些事情，用饶舌表达自己当下的感受。</p>\n<p>Casper 的设计理念最初源于 Vitalik、Vlad 及其他研究者的讨论，已经归纳出的核心原则包括：关键的经济因素、必要的稳健性、普通用户的优先原则。</p>\n                            \n                        ', 0);
