INSERT INTO `coin_dev_member` VALUES (715, 'ethereum', 'Aya Miyaguchi', '以太坊基金会执行总监。', 'https://s2.gongft.com/images/baike/7b60ede6e6ee89fa9a9d4bded68bcafe.jpg', 'https://twitter.com/mi_ayako', 'https://www.linkedin.com/in/amiyaguchi/', 0, '\n                            <p>Aya Miyaguchi 目前是以太坊基金会执行总监，曾在 Kraken 交易平台全权负责日本地区的运营工作。</p>\n                            \n                        ');
INSERT INTO `coin_dev_member` VALUES (716, 'ethereum', 'Joey Zhou', '以太坊开发的鼻祖级人物。', 'https://s2.gongft.com/images/baike/c77980f9b5e0396f418985756ab87902.jpg', 'https://twitter.com/josephzhou', 'https://www.linkedin.com/in/josephyzhou/', 0, '\n                            <p>Joey Zhou 是以太坊开发的鼻祖级人物，早在 2014 年，编写了以太坊上的首个节点实现，并为 Geth、Go-Ethereum、CPP-Ethereum 协议的开发做出了重要贡献。另外，他是 Cyborgenic 咨询公司的管理合伙人，专注于金融科技创新，亦是一位活跃的天使投资人和创业顾问。</p>\n                            \n                        ');
INSERT INTO `coin_dev_member` VALUES (717, 'ethereum', 'Péter Szilágyi', '以太坊核心开发者。', 'https://s2.gongft.com/images/baike/78964ccd6dfea14bf93ba0bb85593659.jpg', 'https://twitter.com/peter_szilagyi', 'https://www.linkedin.com/in/karalabe/', 0, '\n                            <p>Péter Szilágyi，代号 Karalabe，以太坊的核心开发者，另外，他也是分布式通讯应用 Project Iris 的作者、RegionRank 创始人、Ether API 共同创建者。</p>\n                            \n                        ');
INSERT INTO `coin_dev_member` VALUES (718, 'ethereum', 'Greg Colvin', '号称「以太坊甘道夫」。', 'https://s2.gongft.com/images/baike/d4992988ae2587bab1fdac0a1c972396.jpg', 'https://twitter.com/greg_colvin', 'https://www.linkedin.com/in/gregcolvin/', 0, '\n                            <p>「以太坊甘道夫」之称的工程师 Greg Colvin，现为 Algorand 项目理论团队成员，凭借三十多年的计算机软件研发经验，帮助项目创建原子交换、智能合约、虚拟机等。Colvin 拥有康奈尔大学的定量心理学博士学位，以及科罗拉多大学心理学和计算机科学学士学位，并在 ANSI/ISO c++标准委员会任职多年，在企业软件开发公司甲骨文是负责创建 Oracle Java 虚拟机的团队的主要成员，最近参与了以太坊虚拟机性能的改进，并致力于提高以太坊生态系统中流程的透明度。</p>\n                            \n                        ');
INSERT INTO `coin_dev_member` VALUES (719, 'ethereum', 'Fabian Vogelsteller', 'LUKSO 创始人，以太坊项目从事 Ðapp 开发。', 'https://s2.gongft.com/images/baike/aca18a88aa777d83bbe989adc84daa59.jpg', 'https://twitter.com/feindura', 'https://www.linkedin.com/in/fabian-vogelsteller-46365042/', 0, '<p>Fabian Vogelsteller 是 LUKSO 创始人、作者，从前在 Mist、Web3.js 以及以太坊项目从事 &ETH;app 开发。</p>\n');
INSERT INTO `coin_dev_member` VALUES (720, 'ethereum', 'Raul Jordan', 'Prysmatic Labs 联合创始人，以太坊开发者。', 'https://s2.gongft.com/images/baike/aaaa000ca9c606014b15771281620c77.jpg', 'https://twitter.com/raulitojordan', '', 0, '<p>Raul Jordan 是 Prysmatic Labs 联合创始人，也兼任 zk Capital 合伙人，毕业于哈佛，区块链工程师、以太坊开发者，专注于 Prysmatic Labs 分片技术开发。</p>\n');
INSERT INTO `coin_dev_member` VALUES (721, 'ethereum', '平井洋一', '以太坊形式化验证工程师，负责虚拟机项目。', 'https://s2.gongft.com/images/baike/f9e61c8ac1daf0e7c646b43cb5ebffae.jpg', 'https://twitter.com/pirapira', '', 0, '\n                            <p>以太坊形式化验证工程师，负责虚拟机项目。</p>\n                            \n                        ');
INSERT INTO `coin_dev_member` VALUES (722, 'ethereum', 'Justin Drake', '以太坊的研究分析师。', 'https://s2.gongft.com/images/baike/26ab39cb5a23b8dc2dfc9a0384da8157.jpg', 'https://twitter.com/drakefjustin', 'https://www.linkedin.com/in/drakefjustin', 0, '\n                            <p>Justin Drake 住在英国剑桥，拥有英国剑桥大学数学系学士和硕士学位。他于 2013 年创立了剑桥比特币聚会小组，并于 2014 年离开了他作为程序员、FPGA 工程师的职位，专心研究区块链领域。2015 年，他运营了比特币 ATM，并成立了 Duo Money，一家为 OpenBazaar 提供网络界面的公司。而现在，他是以太坊基金会的研究分析师，专注于分片。</p>\n<p>他在 18 岁之后定居英国剑桥，是法国人也是意大利人，妻子是俄罗斯人。父亲是一名工程师，英国人，而母亲是一名心理师，阿根廷人。可见 Justin Drake 的成长背景与生活经验十分国际化，自幼在法国长大，中学时期在中国，此后在英国。</p>\n                            \n                        ');
INSERT INTO `coin_dev_member` VALUES (723, 'ethereum', 'Karl Floersch', '以太坊核心开发者。', 'https://s2.gongft.com/images/baike/7240a3cf09c18ab2ac8e145d70f59a7d.jpg', 'https://twitter.com/karl_dot_tech', 'https://www.linkedin.com/in/karlfloersch', 0, '\n                            <p>以太坊核心开发者，年仅 23 岁，主要参与开发以太坊 Casper 以及权益证明机制 PoS 运算法则。除研究 Casper 以外，他爱好冥想和即兴饶舌，从小受到印度教的熏陶，通过冥想来思考和感悟一些事情，用饶舌表达自己当下的感受。</p>\n<p>Casper 的设计理念最初源于 Vitalik、Vlad 及其他研究者的讨论，已经归纳出的核心原则包括：关键的经济因素、必要的稳健性、普通用户的优先原则。</p>\n                            \n                        ');
