INSERT INTO `coin_dev_member` VALUES (816, 'tezos', 'Arthur Breitman', 'Tezos 联合创始人兼首席技术官。', 'https://s2.gongft.com/images/baike/b6109ab905bcef4049a0ea3c382a9235.jpg', 'https://twitter.com/arthurb', '', 0, '\n                            <p>Arthur Breitman 是 Tezos 联合创始人兼首席技术官，曾在高盛、摩根士丹利担任定量分析师。</p>\n                            \n                        ');
INSERT INTO `coin_dev_member` VALUES (817, 'tezos', 'Kathleen Breitman', 'Tezos 联合创始人兼首席执行官。', 'https://s2.gongft.com/images/baike/fd458c71458b69ed136c957d97fb3cb7.jpg', 'https://twitter.com/breitwoman', 'https://www.linkedin.com/in/breitwoman/', 0, '\n                            <p>Kathleen Breitman 是 Dynamic Ledger Solutions 首席执行官，亦是 Tezos 区块链的开发商。此前，她在由近百家金融公司组成的区块链联盟 R3 担任高级战略助理，还曾在埃森哲、Bridgewater 协会、《华尔街日报》工作。她毕业于康奈尔大学。</p>\n                            \n                        ');
