INSERT INTO `coin_dev_member` VALUES (833, 'bitcoin-cash-sv', 'Steve Shadders', '技术总监', '', '', '', 0, '<p>负责项目监管事宜，并与赞助商和业内人士进行联络。史蒂夫于2011年开始投身比特币事业，为比特币的发展贡献力量。他是早期首批开源矿池引擎的开发设计者，也曾是BitcoinJ创始团队成员。</p>\n');
INSERT INTO `coin_dev_member` VALUES (834, 'bitcoin-cash-sv', 'Daniel Connolly', '首席开发员', '', '', '', 0, '<p>在加入nChain前曾在企业系统工作了20年，并在多个联合国机构担任过高级信息技术（IT）专家。多年来，丹尼尔一直默默为比特币的发展贡献力量，为Electron Cash项目提供技术支持，也是BitcoinJ-Cash项目的主要开发者之一。</p>\n');
INSERT INTO `coin_dev_member` VALUES (835, 'zcash', 'Zooko Wilcox', 'Zcash 创始人兼首席执行官。', 'https://s2.gongft.com/images/baike/214475adbb11a9cd28cb962ba8215d49.jpg', 'https://twitter.com/zooko', 'https://www.linkedin.com/in/zookow/', 0, '\n                            <p>Zooko Wilcox 是 Zcash 创始人兼首席执行官，从前是 Least Authority 首席执行官，负责开发、安全、运营和客户支持等领域。他在安全分布式系统，开源软件及初创公司方面拥有 20 多年的丰富经验，以 DigiCash、Mojo Nation、ZRTP、Zooko\'s Triangle 等项目作品而闻名。另外，他也是 Tahoe-LAFS 项目的联合创始人之一。</p>\n                            \n                        ');
INSERT INTO `coin_dev_member` VALUES (836, 'zcash', 'Nathan Wilcox', 'CTO', 'https://s2.gongft.com/image/20190624/0991561360315744.png', 'https://twitter.com/least_nathan', '', 0, '<p>Nathan致力于开放，安全的技术，在软件开发，性能分析和安全审计方面拥有超过10年的经验。在加入Zcash以前，他负责Least Authroity的开发工作，他领导了对以太坊、SpiderOak、Cryptocat、GlobalLeaks和其它项目的安全审计工作。出于乐趣，他喜欢为Python、Rust和以太坊开发应用工具。</p>\n');
INSERT INTO `coin_dev_member` VALUES (837, 'zcash', 'Jack Gavigan', 'COO', 'https://s2.gongft.com/image/20190624/4441561360447849.png', 'https://twitter.com/JackGavigan', '', 0, '<p>Jack在金融技术，网络安全和交易系统方面拥有丰富的经验，曾在德意志银行，瑞士信贷银行和摩根士丹利（也担任过交易员）工作。&nbsp; &nbsp; Jack做过英国政府数字货币和区块链技术方面的顾问。</p>\n');
INSERT INTO `coin_dev_member` VALUES (838, 'zcash', 'Ian Miers', 'Zcash 联合创始人。', 'https://s2.gongft.com/images/baike/70bafe850bed50dcb8c7cfc0428d46ca.jpg', 'https://twitter.com/secparam', 'https://www.linkedin.com/in/ian-miers-27240827/', 0, '\n                            <p>Ian Miers 是康奈尔大学博士后，致力于匿名系统的密码学专家。他是 ZCash 联合创始人，以及 ZeroCash 论文的作者。</p>\n                            \n                        ');
INSERT INTO `coin_dev_member` VALUES (839, 'zcash', 'Andrew Miller', 'IC3 副主任、Zcash 基金会的董事成员。', 'https://s2.gongft.com/images/baike/c897371b272286dfd23634defe3b7a5a.jpg', 'https://twitter.com/socrates1024', '', 0, '\n                            <p>Andrew Miller 是伊利诺伊大学 Urbana-Champaign 分校计算机科学专业相关的助理教授，也是加密货币与智能合约倡议组织 IC3 副主任、Zcash 基金会的董事成员。</p>\n<p>另外，他在马里兰大学网络安全中心得到了博士学位。其研究兴趣主要集中在计算机安全上，结合编程，专注于安全分布式系统和加密货币的设计。</p>\n                            \n                        ');
INSERT INTO `coin_dev_member` VALUES (840, 'zcash', 'Zirui Zhang', 'Zcash 亚太社区的业务拓展。', 'https://s2.gongft.com/images/baike/ad96dce95f5cc146be1e217b173ba97a.jpg', 'https://twitter.com/zirui_z', '', 0, '\n                            <p>Zirui Zhang 是早期的比特币矿工和投资者，游走在亚洲、美国之间，密码世界的极端主义者。她非常关注隐私和互联网安全，并在数字货币领域工作了五年多时间，与亚洲加密货币社区有着千丝万缕的联系。</p>\n                            \n                        ');
INSERT INTO `coin_dev_member` VALUES (841, 'kcs', 'Michael Gan', 'KuCoin 首席执行官。', 'https://s2.gongft.com/images/baike/6778ab5c564ae3371bdb5e3708864ef8.jpg', '', '', 0, '\n                            <p>Michael Gan，现任 KuCoin 首席执行官，此前，他是阿里巴巴集团蚂蚁金服的前技术专家，金融解决方案的资深人士，以及 MikeCRM、KF5.COM 等互联网巨头的高级合伙人。</p>\n<p>他早在 1998 年，即是网易免费个人网站托管服务的首批用户，具备很强的学习能力、敏捷开发经验，热衷于开发团队内部高效和小规模的协作。</p>\n                            \n                        ');
