INSERT INTO `coin_dev_member` VALUES (844, 'iota', 'Dominik Schiener', 'IOTA 联合创始人。', 'https://s2.gongft.com/images/baike/6a88bd859a19dad5d0151f20053cfc50.jpg', 'https://twitter.com/DomSchiener', 'https://www.linkedin.com/in/schiener/', 0, '\n                            <p>Dominik Schiener 是 IOTA 联合创始人，为人务实、直接。他目前正忙于开展几个区块链与智能合约的项目，以创建具有社会影响力的去中心化应用。他尤其对于新的治理解决方案特别感兴趣，以创建更高效、便宜和透明的系统。</p>\n                            \n                        ');
INSERT INTO `coin_dev_member` VALUES (845, 'iota', 'Serguei Popov', '科学顾问，也是IOTA 联合创始人。', 'https://s2.gongft.com/images/baike/a74176c7f24899993e4962e9a6ce8a3e.jpg', '', 'https://www.linkedin.com/in/serguei-popov-631bb762/', 0, '<p>Serguei Popov 是 IOTA 联合创始人，拥有莫斯科国立大学的数学博士学位，与 Sergey Ivancheglo、DavidS&oslash;nsteb&oslash;、Dominik Schiener 共同创立 IOTA，具体负责理论和概念方面的工作。</p>\n');
INSERT INTO `coin_dev_member` VALUES (846, 'iota', 'David Sønstebø', '董事会联合主席兼创始人', 'https://s2.gongft.com/image/20190622/8181561194438549.png', 'https://twitter.com/DavidSonstebo', 'https://www.linkedin.com/in/david-s%C3%B8nsteb%C3%B8-760319a5', 0, '<p>我是一个连续创业者，对任何能够推动人类进步的技术都充满激情。我积极参与物联网、分布式账本/区块链/tangle技术、智能城市、数字身份、电子治理和创新硬件设计。</p>\n');
INSERT INTO `coin_dev_member` VALUES (847, 'iota', 'Ralf Rottmann', '董事会成员', 'https://s2.gongft.com/image/20190622/2491561194768328.png', 'https://twitter.com/ralf', 'https://de.linkedin.com/in/rottmann/de', 0, '<p>拉尔夫是一位连续创业者，他将自己的最后一家公司卖给了阿尔卡特朗讯，并且是德国领先的物联网SI产品的grandcentrix的联合创始人。</p>\n');
INSERT INTO `coin_dev_member` VALUES (848, 'iota', 'Lewis Freiberg', '生态系统负责人，高级软件工程师', '', 'https://twitter.com/lewisfreiberg?lang=en', 'https://www.linkedin.com/in/lewisfreiberg/', 0, '');
INSERT INTO `coin_dev_member` VALUES (849, 'maker', 'Rune Christensen', '联合创始人兼首席执行官', 'https://s2.gongft.com/images/baike/4996256b09df187c28d75021f1e5aea5.jpg', 'https://twitter.com/runekek', 'https://www.linkedin.com/in/rune-christensen-5b5220ab/', 0, '<p>Rune Christensen是MakerDAO联合创始人兼首席执行官。由MakerDAO发布的 DAI，是世界上首个在以太坊区块链上使用的稳定币，旨在透过应对市场变化的智能合约系统去除价格波动。他自2015年以来，一直致力于建立分布式自治组织的愿景，以及稳定数字货币的经济基础。此前，他曾参与创办Try China国际招聘公司。</p>\n');
INSERT INTO `coin_dev_member` VALUES (850, 'maker', 'Andy Milenius', '首席技术官', 'https://s2.gongft.com/images/baike/fa22646b47953682b7e0839bbf1494a3.jpg', 'https://twitter.com/RealZandy', 'https://www.linkedin.com/in/andy-milenius-919922123/', 0, '<p>Andy Milenius是MakerDAO首席技术官，对于分布是自治组织及分散治理和引导在线社区的机制设计特别感兴趣。此前，他在Amazon Web Services工作，拥有密歇根大学的计算机科学学位，出生在美国底特律。</p>\n');
INSERT INTO `coin_dev_member` VALUES (851, 'maker', 'Matt Richards', '董事长', '', '', '', 0, '');
INSERT INTO `coin_dev_member` VALUES (852, 'maker', '潘超', 'MakerDAO 经济研究员兼中国社区负责人。', 'https://s2.gongft.com/images/baike/14d94aea1b9e00932694f2569f9c644b.jpg', 'https://twitter.com/TheChaology', 'https://www.linkedin.com/in/chao-pan-30294b86/', 0, '\n                            <p>潘超是 MakerDAO 经济研究员、分析师，并负责中国区业务。</p>\n                            \n                        ');
INSERT INTO `coin_dev_member` VALUES (853, 'maker', 'Gregory Di Prisco', 'Distributed Capital 合伙人，在 Maker 负责商务拓展。', 'https://s2.gongft.com/images/baike/e2ee815eb83b46bbe53136c75d56da8e.jpg', '', 'https://www.linkedin.com/in/gregory-di-prisco-0672a9125/', 0, '\n                            <p>Greg Di Prisco 是 Distributed Capital 合伙人，在 Maker 负责商务拓展，也是 EtherIndex 的联合创始人兼首席执行官。</p>\n                            \n                        ');
INSERT INTO `coin_dev_member` VALUES (854, 'maker', 'Henry Doe', 'MakerDAO 设计主管。', 'https://s2.gongft.com/images/baike/6401dd1351a4009c70099f0d9fbb9fca.jpg', 'https://twitter.com/hcdoe', 'https://www.linkedin.com/in/henrydoe/', 0, '\n                            <p>Henry Doe 现任 MakerDAO 设计主管，也是 POINT 产品设计工作室的创始人，服务于包括 MakerDAO、Lloyd\'s of London、Deutsche Bank、MoneyTailor、Vanquis Bank 等客户。</p>\n                            \n                        ');
INSERT INTO `coin_dev_member` VALUES (855, 'maker', 'Martin Lundfall', 'Dapphub、MakerDAO、CirclesUBI 形式化验证的研究员。', 'https://s2.gongft.com/images/baike/dbb9d4d53e26d3814a7f25d3fff63f39.jpg', 'https://twitter.com/martinlundfall', '', 0, '\n                            <p>Martin Lundfall 在 Dapphub、MakerDAO、CirclesUBI 做形式化验证的研究员。在加入 Dapphub 以前，他是去中心化能源市场 TransActive Grid 主要开发者，后来发展成为 Grid +。 他的背景是数学、计算机科学、物理等。</p>\n                            \n                        ');
INSERT INTO `coin_dev_member` VALUES (856, 'maker', 'Joseph Quintilian', 'MakerDAO 交易主管，Distributed Capital Partners 合伙人。', 'https://s2.gongft.com/images/baike/80d16d59bda998a259b0b029a6e7efd8.jpg', 'https://twitter.com/joeqtrader', 'https://www.linkedin.com/in/joseph-quintilian-6a1a80b2/', 0, '\n                            <p>Joseph Quintilian 是 MakerDAO 交易主管，Distributed Capital Partners 合伙人，以及 Etherindex 的联合创始人兼首席财务官，创建了以太坊 ETF。</p>\n                            \n                        ');
INSERT INTO `coin_dev_member` VALUES (857, 'maker', 'Coulter Mulligan', 'MakerDAO 市场推广负责人。', 'https://s2.gongft.com/images/baike/49c6081123728796c4ad7b90b1365e9a.jpg', 'https://twitter.com/CoulterM', 'https://www.linkedin.com/in/coultermulligan/', 0, '\n                            <p>Coulter Mulligan 现任 MakerDAO 市场推广负责人，他富有想象力、责任感，且经验丰富。</p>\n<p>此前，他在 GoPro 负责全球的社会化营销，协调国际团队和机构，通过本地化渠道实施全球战略，使 GoPro 保持在社交媒体的最前沿。用数据驱动的洞察力，确定关键主题与目标。再之前，他在 Tesla 负责内容营销，与多个内部部门合作创建、开发、制作视频和照片广告系列。</p>\n                            \n                        ');
INSERT INTO `coin_dev_member` VALUES (858, 'maker', 'Cyrus Younessi', 'MakerDAO 风险管理。', 'https://s2.gongft.com/images/baike/0e1f6ef20560b795055ed9975f75ce44.jpg', 'https://twitter.com/cyounessi1', 'https://www.linkedin.com/in/cyounessi/', 0, '\n                            <p>Cyrus Younessi 于 2019 年 2 月加入 MakerDAO 团队，做风险管理。此前，他曾在 Scalar Capital 任职研究分析与交易总监，在 Cumberland Mining 做投资分析师，在 DRW 做固定收益交易员。</p>\n                            \n                        ');
INSERT INTO `coin_dev_member` VALUES (859, 'maker', 'Mariano Conti', 'MakerDAO 开发工程师。', 'https://s2.gongft.com/images/baike/546c1b373ed92c290a12c40ca1b261b0.jpg', 'https://twitter.com/nanexcool', 'https://www.linkedin.com/in/marianoconti/', 0, '\n                            <p>Mariano Conti 在 MakerDAO 开发工程师，主要负责开发 Dai 稳定币及信用系统，从智能合约到价格波动管理，从基础设施到用户界面。另外，他也参与 DappHub 计划的一部分。</p>\n                            \n                        ');
INSERT INTO `coin_dev_member` VALUES (860, 'ht', '李林', '董事长/创始人/CEO', 'https://s2.gongft.com/image/20190411/7301554965985373.png', '', 'https://www.linkedin.com/in/leon-li-13621bb8/', 0, '李林先生毕业于清华大学自动化系，连续创业者，曾就职于全球最大的数据库厂商Oracle。李林先生极具互联网行业前瞻眼光，2013年创办了火币网，并将火币网打造成为全球领先的区块链资产金融服务商。');
INSERT INTO `coin_dev_member` VALUES (861, 'ht', '杜均', '联合创始人', 'https://s2.gongft.com/images/baike/549694e380affeb9e0ca79986eebac55.png', '', '', 0, '<p>杜均先生是全球最早从事区块链产业投资的专业投资人，也是知名的数字资产投资和管理人。他曾供职于腾讯、康盛创想等知名互联网企业，并联合创建了数字货币交易平台火币网、区块链专业媒体金色财经等公司，具有专业的行业知识、丰富的创业经验和行业资源，擅长市场运营、资本运作及企业孵化，投资的区块链项目已达数十个。</p>\n');
INSERT INTO `coin_dev_member` VALUES (862, 'ht', '程显峰', 'CTO', 'https://s2.gongft.com/image/20190411/2531554965209229.png', '', '', 0, '<p><span style=\"font-size:10.5pt\"><span style=\"layout-grid-mode:char\"><span style=\"font-family:等线\"><span style=\"font-size:11.0pt\"><span style=\"font-family:&quot;微软雅黑&quot;,sans-serif\">程显峰先生负责火币集团技术研发中心管理工作，中国国家互联网金融安全技术专家委员会委员，中国计算机学会区块链专业委员会委员。曾任OneAPM公司首席运营官，积木盒子技术VP，是MongoDB和Docker在中国早期的推动者。</span></span></span></span></span></p>\n');
INSERT INTO `coin_dev_member` VALUES (863, 'ht', '朱嘉伟', 'COO', 'https://s2.gongft.com/image/20190411/4961554965078027.png', '', '', 0, '<p><span style=\"font-size:11.0pt;\"><span style=\"font-family:&quot;微软雅黑&quot;,sans-serif;\">朱嘉伟先生负责火币集团运营中心管理工作，曾就职于 Oracle、凯捷咨询。拥有丰富的公司管理、数据分析实践经验及业务运营管理经验。</span></span></p>\n');
INSERT INTO `coin_dev_member` VALUES (864, 'ht', '李书沸', 'CFO', 'https://s2.gongft.com/image/20190411/3851554965301364.png', '', '', 0, '<p><span style=\"font-family: 微软雅黑, sans-serif; font-size: 11pt;\">李书沸先生负责火币集团财务、并购及董事秘书工作。曾任知名数字资产服务集团CFO、交易所业务CEO，以及百富环球（HK.0327）CFO，在企业管理、战略部署及执行、国际业务拓展、金融科技及电子支付的资本市场拥有超过十五年丰富的专业经验。</span></p>\n');
INSERT INTO `coin_dev_member` VALUES (865, 'ht', '吴树鹏', 'CSO', 'https://s2.gongft.com/image/20190411/9781554965534606.png', 'https://twitter.com/olivewang839', 'https://www.linkedin.com/in/olivia-wang-63143229/', 0, '<p style=\"margin-right:5.0cm; margin:0cm 0cm 0.0001pt; text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"layout-grid-mode:char\"><span style=\"font-family:等线\"><span style=\"font-size:11.0pt\"><span style=\"font-family:&quot;微软雅黑&quot;,sans-serif\">吴树鹏先生负责火币集团安全与风控方面的管理工作，曾就职于滴滴出行、国际化咨询公司、绿盟 科技与政府机构。拥有丰富的信息安全、业务安全、风险管理方面的技术研究与管理经验。</span></span></span></span></span></p>\n');
