INSERT INTO `coin_dev_member` VALUES (885, 'blockstack', 'Muneeb Ali', 'Blockstack 联合创始人。', 'https://s2.gongft.com/images/baike/a8c0bf1f499a157c6940efe128e6b0cd.jpg', '', '', 0, '\n                            <p>Muneeb Ali 是 Blockstack 联合创始人，旨在搭建分布式应用的全栈解决方案。他曾在 TEDx 等论坛上发表演讲，传播加密数字货币、区块链，并撰写了大量相关主题的学术刊物与白皮书。另外，他还拥有普林斯顿大学分布式系统的博士学位。</p>\n                            \n                        ');
INSERT INTO `coin_dev_member` VALUES (886, 'blockstack', 'Ryan Shea', 'Blockstack 联合创始人。', 'https://s2.gongft.com/images/baike/6db0a757134595611ea64d13813496d0.jpg', '', '', 0, '\n                            <p>Ryan Shea，Blockstack 联合创始人，也是 Lightning、Embark 等区块链项目种子轮的投资者。此前，他参与创立了名为 GraphMuse 软件即服务的创业公司。他是普林斯顿创业俱乐部的会长，毕业于普林斯顿大学机械和航空航天工程学位，辅修计算机科学。他在密码学和区块链技术方面写过几个流行的开源库，喜欢建立公司、开发产品和发展社区。</p>\n                            \n                        ');
